import './App.css';
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom";

function App() {

  let { id } = useParams();
  console.log(id);
  const [token, setToken] = useState("");
  const limit = 3;
  let count = 0;
  if(localStorage.getItem("count")===null){
    localStorage.setItem("count",`${count}`);
  }
  useEffect(() => {
    if (typeof (id) != 'undefined' && id != null && id != "" ) {
      document.cookie = id;
      setToken(id);
      // console.log(token);
      // localStorage.removeItem("count");
      localStorage.setItem("count",`${0}`);
      console.log("success host",window.location.host)
      // redirect("http://localhost:3000/success")
    } else {
      if(parseInt(localStorage.getItem("count"))<=limit){
        localStorage.setItem("count",`${parseInt(localStorage.getItem("count"))+1}`)
        redirect(`http://localhost:2000/cookie/${window.location.href.replace(window.location.protocol+"//","")}`);
      }
      else if(parseInt(localStorage.getItem("count"))>3){
        localStorage.setItem("count",`${0}`);
        redirect("http://localhost:3000/error")
      }
    }
  }, [])

  const redirect = url => {
    console.log(url);
    window.location.replace(url);
  }

  return (
    <div className="App">
      <header className="App-header">
        {"Token is "}
        {token}
      </header>
    </div>
  );

}

export default App;
