import './App.css';
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom";

function App() {

  let { id } = useParams();
  console.log(id);
  const [token, setToken] = useState("");
  const limit = 3;
  if(localStorage.getItem("count")===null){
    localStorage.setItem("count",'0');
  }
  useEffect(() => {
    if (typeof (id) !== 'undefined' && id !== null && id !== "" ) {
      document.cookie = id;
      setToken(id);
      console.log(token);
      localStorage.setItem("count",`${0}`);
      redirect(`http://${window.location.host}/success`)
    } else {
      if(parseInt(localStorage.getItem("count"))<limit){
        localStorage.setItem("count",`${parseInt(localStorage.getItem("count"))+1}`)
        redirect(`http://localhost:2000/cookie/${window.location.host}`);
      }
      else if(parseInt(localStorage.getItem("count"))>=3){
        localStorage.setItem("count",`${0}`);
        redirect(`http://${window.location.host}/error`)
      }
    }
  }, [])

  const redirect = url => {
    isSiteOnline(`http://localhost:2000`,function(found){
    if(found) {
      console.log(url,"is online");
    window.location.replace(url);
    }
    else {
      console.log(url,"is offline");
    }
    })
  }

  function isSiteOnline(url,callback) {
    // try to load favicon
    var timer = setTimeout(function(){
        // timeout after 5 seconds
        callback(false);
    },5000)

    var img = document.createElement("img");
    img.onload = function() {
        clearTimeout(timer);
        callback(true);
    }

    img.onerror = function() {
        clearTimeout(timer);
        callback(false);
    }

    img.src = url+"/favicon.ico";
}

  return (
    <div className="center">
      <header className="App-header">
        {"Token is "}
        {token}
      </header>
    </div>
  );

}

export default App;
