import './App.css';
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom";
import axios from "axios";
function App() {

  let { id } = useParams();
  console.log(id);
  const [token, setToken] = useState("");
  const limit = 3;
  if(localStorage.getItem("count")===null){
    localStorage.setItem("count",'0');
  }

  function getStatus(){
    axios.get("http://localhost:2000/")
            .then(response => console.log(response.status))
            .catch((error)=>{
              console.log("error ",error)
            });
  }

  useEffect(() => {
    getStatus()
    if (typeof (id) !== 'undefined' && id !== null && id !== "" ) {
      document.cookie = id;
      setToken(id);
      console.log(token);
      localStorage.setItem("count",`${0}`);
      redirect(`http://${window.location.host}/success`)
    } else {
      if(parseInt(localStorage.getItem("count"))<limit){
        localStorage.setItem("count",`${parseInt(localStorage.getItem("count"))+1}`)

        // redirect(`http://localhost:2000/cookie/${window.location.host}`);
      }
      else if(parseInt(localStorage.getItem("count"))>=3){
        localStorage.setItem("count",`${0}`);
        redirect(`http://${window.location.host}/error`)
      }
    }
  }, [])

  const redirect = url => {
    window.location.replace(url);
  }



  return (
    <div className="center">
      <header className="App-header">
        {"Token is "}
        {token}
      </header>
    </div>
  );

}

export default App;
